// src/pages/PropertyList.js
import React, { useEffect, useState } from 'react';

// Dummy descriptions and tips for demo purposes
const descriptions = {
  SS13: `<strong>SS13</strong> 小区全称为The Grand Subang SS13...`,
  SS15: `<strong>SS15</strong> 小区全称为The Grand Subang Jaya SS15...`,
  // Add other location descriptions
};

const tips = {
  SS13: `距离莫纳什大学较近，生活便利。`,
  SS15: `地处小吃街，楼下各国美食应有尽有。`,
  // Add other location tips
};

const getProperties = async () => {
  console.log('Fetching properties...'); // Debugging line
  try {
    // Fetching all properties without filtering by location
    const response = await fetch('/api/properties');
    console.log('Response received:', response); // Debugging line

    // Check if the response is OK and convert it to JSON
    if (response.ok) {
      const data = await response.json();
      console.log('Data received:', data); // Debugging line

      // Check if data is not empty
      if (data && data.length > 0) {
        setProperties(data);
        setErrorMessage('');
      } else {
        setProperties([]);
        setErrorMessage('未找到任何房源。');
      }
    } else {
      setErrorMessage('无法加载房源。');
      console.error('Response error:', response.status, response.statusText);
    }
  } catch (error) {
    console.error('获取房源时出错:', error);
    setErrorMessage('加载房源失败，请稍后重试。');
  }
};


export default PropertyList;
