// src/pages/PropertyList.js
import React, { useEffect, useState } from 'react';

// Dummy descriptions and tips for demo purposes
const descriptions = {
  SS13: `<strong>SS13</strong> 小区全称为The Grand Subang SS13...`,
  SS15: `<strong>SS15</strong> 小区全称为The Grand Subang Jaya SS15...`,
  // Add other location descriptions
};

const tips = {
  SS13: `距离莫纳什大学较近，生活便利。`,
  SS15: `地处小吃街，楼下各国美食应有尽有。`,
  // Add other location tips
};

function PropertyList() {
  const [properties, setProperties] = useState([]);
  const [selectedLocation, setSelectedLocation] = useState('all');
  const [errorMessage, setErrorMessage] = useState('');
  const [description, setDescription] = useState('');
  const [tip, setTip] = useState('');

  // Fetch properties when component mounts or selectedLocation changes
  useEffect(() => {
    getProperties();
  }, [selectedLocation]);

  const getProperties = async () => {
    try {
      const response = await fetch(`/api/properties${selectedLocation !== 'all' ? `?location=${selectedLocation}` : ''}`);
      const data = await response.json();

      if (!data || data.length === 0) {
        setProperties([]);
        setErrorMessage('未找到该位置的房源。');
      } else {
        setProperties(data);
        setErrorMessage('');
      }
    } catch (error) {
      console.error('获取房源时出错:', error);
      setErrorMessage('加载房源失败，请稍后重试。');
    }
  };

  const handleLocationClick = (location) => {
    setSelectedLocation(location);
    setDescription(descriptions[location]);
    setTip(tips[location]);
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return `${date.getFullYear()}年${(date.getMonth() + 1).toString().padStart(2, '0')}月${date.getDate().toString().padStart(2, '0')}日`;
  };

  return (
    <div>
      <nav className="nav-links">
        {['SS13', 'SS15', 'CasaTiara', 'IconCity', 'Greenfield', 'GeoLake', 'GeoSense', 'Union', 'Edumetro'].map((location) => (
          <a href="#" key={location} onClick={() => handleLocationClick(location)}>{location}</a>
        ))}
      </nav>

      {description && <div className="description" dangerouslySetInnerHTML={{ __html: description }} />}
      {tip && <div className="tip">{tip}</div>}

      <main id="property-list">
        {errorMessage && <p>{errorMessage}</p>}
        {properties.map((property) => (
          <div key={property.id} className={`property-card ${property.rented ? 'rented' : ''}`}>
            <div className="image-container">
              <img src={property.images && property.images[0] ? `/api/images/${property.images[0]}` : 'default-image.jpg'} alt="房源图片" />
              {property.rented && <div className="overlay-text">不好意思，此单位已经出租！</div>}
            </div>
            <div className="property-details">
              <h2>{property.title || '无可用标题'}</h2>
              <p className="price">价格: {property.price ? `RM${property.price}` : '未提供价格'}</p>
              <p>卧室: {property.rooms || '无卧室信息'}</p>
              <p>浴室: {property.bathrooms || '无浴室信息'}</p>
              <p>描述: {property.description || '无描述'}</p>
              <p className="available-date">可入住时间: {property.availableDate ? formatDate(property.availableDate) : '无可入住日期信息'}</p>
              <div className="meta-info">
                {property.tags && property.tags.split(',').map((tag, index) => <span key={index}>{tag}</span>)}
              </div>
            </div>
          </div>
        ))}
      </main>
    </div>
  );
}

export default PropertyList;
